// Filter and select random word from Google Sheets data
const items = $input.all();
const wordList = [];

// Extract words from Google Sheets response
for (const item of items) {
  if (item.json.A && typeof item.json.A === 'string' && item.json.A.trim().length > 0) {
    const word = item.json.A.trim().toLowerCase();
    // Skip potential headers
    if (!['word', 'english', 'words'].includes(word)) {
      wordList.push(word);
    }
  }
}

// Select random word
if (wordList.length === 0) {
  throw new Error('No valid words found in the sheet');
}

const selectedWord = wordList[Math.floor(Math.random() * wordList.length)];

return [{
  json: {
    selectedWord: selectedWord,
    totalWords: wordList.length
  }
}];

// Parse OpenAI response and prepare data
const item = $input.first();
let parsedResponse;

try {
  // Extract the AI response content
  const content = item.json.choices[0].message.content;
  parsedResponse = JSON.parse(content);

  // Validate the response structure
  if (!parsedResponse.category || !Array.isArray(parsedResponse.related_words)) {
    throw new Error('Invalid response structure from AI');
  }

} catch (error) {
  // Fallback response on error
  parsedResponse = {
    category: 'error',
    related_words: [],
    error: 'Failed to parse AI response: ' + error.message
  };
}

// Get the original word from previous step
const selectedWord = $('Select Random Word').first().json.selectedWord;

// Prepare data for Google Sheets
const currentDate = new Date().toISOString().split('T')[0];
const sheetRows = [];

if (parsedResponse.related_words && parsedResponse.related_words.length > 0) {
  for (const wordPair of parsedResponse.related_words) {
    sheetRows.push({
      Date: currentDate,
      Original_Word: selectedWord,
      Category: parsedResponse.category,
      Dutch_Word: wordPair.dutch,
      English_Word: wordPair.english
    });
  }
} else {
  // Add error row if no words generated
  sheetRows.push({
    Date: currentDate,
    Original_Word: selectedWord,
    Category: 'error',
    Dutch_Word: '',
    English_Word: parsedResponse.error || 'No words generated'
  });
}

return [{
  json: {
    selectedWord: selectedWord,
    parsedResponse: parsedResponse,
    sheetRows: sheetRows,
    currentDate: currentDate
  }
}];

// Split the data into individual rows for Google Sheets append
const item = $input.first();
const sheetRows = item.json.sheetRows;

// Convert each row to the format expected by Google Sheets
const output = [];
for (const row of sheetRows) {
  output.push({
    json: {
      Date: row.Date,
      Original_Word: row.Original_Word,
      Category: row.Category,
      Dutch_Word: row.Dutch_Word,
      English_Word: row.English_Word
    }
  });
}

return output;

// Create Discord embed message
const parseData = $('Parse AI Response').first().json;
const selectedWord = parseData.selectedWord;
const parsedResponse = parseData.parsedResponse;
const currentDate = parseData.currentDate;

// Format date for display
const displayDate = new Date(currentDate).toLocaleDateString('en-US', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Build word list for Discord (limit to 8 words)
let wordListText = '';
if (parsedResponse.related_words && parsedResponse.related_words.length > 0) {
  const wordsToShow = parsedResponse.related_words.slice(0, 8);
  for (let i = 0; i < wordsToShow.length; i++) {
    const wordPair = wordsToShow[i];
    wordListText += `${i + 1}. **${wordPair.english}** â†’ ${wordPair.dutch}\n`;
  }
} else {
  wordListText = 'No words generated today.';
}

// Create Discord webhook payload
const discordPayload = {
  embeds: [{
    title: `ðŸ“š Daily English Word: ${selectedWord.charAt(0).toUpperCase() + selectedWord.slice(1)}`,
    description: `Category: **${parsedResponse.category.charAt(0).toUpperCase() + parsedResponse.category.slice(1)}**`,
    color: 3447003, // Blue color
    fields: [
      {
        name: 'ðŸ‡³ðŸ‡± Related Words with Dutch Translations',
        value: wordListText,
        inline: false
      }
    ],
    footer: {
      text: `Generated on ${displayDate}`
    }
  }]
};

// Add error field if there's an error
if (parsedResponse.error) {
  discordPayload.embeds[0].fields.push({
    name: 'âš ï¸ Note',
    value: parsedResponse.error,
    inline: false
  });
}

return [{
  json: discordPayload
}];

// Log workflow completion
const parseData = $('Parse AI Response').first().json;
const sheetResult = $('Update Google Sheet').all();
const discordResult = $('Send to Discord').first();

const logData = {
  timestamp: new Date().toISOString(),
  selectedWord: parseData.selectedWord,
  category: parseData.parsedResponse.category,
  wordsGenerated: parseData.parsedResponse.related_words ? parseData.parsedResponse.related_words.length : 0,
  sheetUpdated: sheetResult.length > 0,
  discordSent: discordResult.json ? true : false,
  errors: parseData.parsedResponse.error || null
};

console.log('Daily Word Workflow Completed:', JSON.stringify(logData, null, 2));

return [{
  json: {
    success: true,
    log: logData,
    finalOutput: {
      category: parseData.parsedResponse.category,
      related_words: parseData.parsedResponse.related_words || [],
      error: parseData.parsedResponse.error || ""
    }
  }
}];
